mapper.update:
UPDATE MEMBER
SET PASSWORD = #{memberPassword}, SET NAME = #{memberName}, SET PHONE = #{memberPassword}, SET EMAIL = #{memberEmail}, SET GRADE = #{memberGrade},
WHERE MEMBERID = #{MEMBERID}

mapper.select:
SELECT CASE WHEN COUNT(MEMBERID) = 1 THEN 0 ELSE 1 END AS
MEMBERID
FROM MEMBER WHERE MEMBERID = #{str}
